import React, {useState, useEffect} from 'react';

import styles from './AddMealForm.module.css';

const AddMealsForm = function () {

const [enteredAmount, setEnteredAmount] = useState(1);

const enteredAmountHandler = (event) => {
// if (event.target.value == 2)
setEnteredAmount(event.target.value)
};


const AddInCart = (e) => {
    e.preventDefault();
  
}

    return (
      <form >
        <span> Amount </span> 
        <input type="number" min="1" max="5" step="1"  value={enteredAmount} onChange={enteredAmountHandler} />
       <div> 
        <button onClick={AddInCart}>+ Add</button>
        </div>
      </form>
    );
};


export default AddMealsForm;
