import { useContext , useEffect} from "react";
import CartIcon from "./CartIcon";
import styles from "./Header.module.css";
import mealsImage from "./assets/meals.jpg";
// import AddMealsForm from "../Meals/MealsItem/AddMealForm";
import cartContext from "../../Store/cart-context";

const Header = function (props) {

  const cartCtx = useContext(cartContext);



const {itemAmount} = cartCtx;
 const numberOfCartItems = itemAmount.reduce((curNumber, item) => {
   return curNumber + item;
 }, 0);
 
console.log(itemAmount)

useEffect(() => {
  if (itemAmount.length === 0) {
    return;
 }
  setBtnIsHighlighted(true);

  const timer = setTimeout(() => {
    setBtnIsHighlighted(false);
  }, 300);
console.log(itemAmount);
  itemAmount  = cartCtx;
 itemAmount = cartCtx.itemAmount;


  return () => itemAmount;
}, []);

  return (
    <>
      <header className={styles.header}>
        <h1>ReactMeals</h1>
        <button className={styles.button}>
          {/* <div className={styles['main-image']}> */}
          <span className={styles.icon}>
            <CartIcon />
          </span>
          Your Cart <span className={styles.badge}>{numberOfCartItems}</span>
        </button>
      </header>
      <div className={styles["main-image"]}>
        <img src={mealsImage} alt="A table full of delicious food!" />
      </div>
    </>
  );
};

export default Header;
