      //   id: action.val.id,
      //   description: action.val.description,
      //   price: action.val.price,
      // },
      items: [
        ...state.items,
        {
          amount: +action.val.amount,
          id: action.val.id,
          description: action.val.description,
          price: action.val.price,
        },
      ],

      totalAmount: totalAmount,
    };

  }

  //  defaultState.items = state.val;
  // defaultState.items = [1];
};
