import { useContext } from "react";
import cartContext from "./cart-context";

const CartProvider = function (props) {
  const addedAmount = useContext(cartContext);
  const [amount, setAmoint] = useState(addedAmount.itemAmount);

const amountHandler = function (amount) {
    addedAmount.itemAmount.push(amount);
    setAmoint(amount)
}


  // const addItem =  function (item=0) {

  //    return item;
  // }

  console.log(addedAmount.addItem);
  return (
    <cartContext.Provider
      value={{
        itemAmount: amount,
        addItem: amountHandler
      }}
    > 
     
      {props.children}
    </cartContext.Provider>
  );
};
export default CartProvider;
