import React, { useState, useEffect, useContext, useRef} from "react";

import styles from "./AddMealForm.module.css";
import cartContext from '../../../Store/cart-context'
import CartProvider from "../../../Store/CartProvider";


const AddMealsForm = function (props) {
  const ctx = useContext(CartProvider);
  const enteredQuantity = useRef();



  // const [enteredAmount, setEnteredAmount] = useState(1);
  const [amointIsValid, setAmointIsValid] = useState(true);

   

  const enteredAmountHandler = (event) => {
    
    // setEnteredAmount(event.target.value);
    console.log(enteredQuantity.current.value)
  };
  //   useEffect(() => {
  //     const enteredAmountHandler = (event) => {
  //       setEnteredAmount(event.target.value);
  //       if (enteredAmount === "2") setAmointIsValid(false);
  //       if (enteredAmount !== "2") setAmointIsValid(true);
  //     };
  //   }, []);

  // console.log(isFinite('ei'))

  const AddInCart = (e) => {
    e.preventDefault();

    if (enteredQuantity.current.value === null || +enteredQuantity.current.value < 1 || +enteredQuantity.current.value > 5) {
      setAmointIsValid(false);
    } else setAmointIsValid(true);


  };

  return (
    <>
    <form>
      <span> Amount </span>
      <input
        type="number"
        min="1"
        max="5"
        step="1"
        defaultValue='1'
        // value={enteredQuantity}
        ref={enteredQuantity}
        onChange={enteredAmountHandler}
      />
      {!amointIsValid && (
        <p className={styles.wrongAmount}>Enter valid amount! (1 to 5)</p>
      )}
      <div>
        <button onClick={AddInCart}>+ Add</button>
      </div>
    </form>
    </>
  );
};

export default AddMealsForm;
